package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.GrainPrice;import com.zhoubi.graindepot.mapper.GrainPriceMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class GrainPriceBiz extends BaseService<GrainPrice>  {	@Autowired	private GrainPriceMapper GrainPriceMapper;	@Override	protected BaseMapper<GrainPrice> getMapper() {		return GrainPriceMapper;	}	public int checkRepeat(Map map) {		return GrainPriceMapper.checkRepeat(map);	}}