package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Individual;import com.zhoubi.graindepot.mapper.IndividualMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;@Servicepublic class IndividualBiz extends BaseService<Individual> {    @Autowired    private IndividualMapper IndividualMapper;    @Override    protected BaseMapper<Individual> getMapper() {        return IndividualMapper;    }    public int checkRepeat(Map param) {        return IndividualMapper.checkRepeat(param);    }    public int updateByIdcardAndGraindepotid(Individual item) {        return IndividualMapper.updateByIdcardAndGraindepotid(item);    }    public List<Individual> listByNameAndGraindepotid(Map map){        return IndividualMapper.listByNameAndGraindepotid(map);    }}