package com.zhoubi.graindepot.biz;import com.zhoubi.graindepot.bean.Prerece;import com.zhoubi.graindepot.mapper.PrereceMapper;import com.zhoubi.graindepot.base.BaseMapper;import com.zhoubi.graindepot.base.BaseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class PrereceBiz extends BaseService<Prerece>  {	@Autowired	private PrereceMapper prereceMapper;	@Override	protected BaseMapper<Prerece> getMapper() {		return prereceMapper;	}	public String getMaxBillcode(Integer graindepotid){		return prereceMapper.getMaxBillcode(graindepotid);	}	public Double getLeftAmount(Map param) {		return prereceMapper.getLeftAmount(param);	}}